import "core-js/modules/esnext.iterator.constructor.js";
import "core-js/modules/esnext.iterator.filter.js";
import "core-js/modules/esnext.iterator.map.js";
import { ref, onMounted } from 'vue';
import axios from 'axios';
import AppHeader from '/AppHeader.vue';
export default {
  name: 'CustomerPage',
  components: {
    AppHeader
  },
  setup() {
    const products = ref([]);
    const showDetails = ref(false);
    const selectedProduct = ref({});
    const categories = ref(['全部', '家居日用', '穿搭服饰', '米面粮油', '生鲜水果', '科技数码', '运动户外']);
    const searchQuery = ref('');
    const deliveryAddress = ref('');
    const purchaseQuantity = ref(1);
    const showPurchaseForm = ref(false);
    const cartCount = ref(0);
    const showCart = ref(false);
    const cartItems = ref([]);
    const showMyOptions = ref(false);
    const showAddressDialog = ref(false);
    const showOrderDialog = ref(false);
    const addresses = ref([]);
    const newAddress = ref({
      uname: '',
      uphone: '',
      addressName: '',
      umail: ''
    });
    const fetchProducts = async (category = '全部') => {
      try {
        const url = category === '全部' ? '/eShop/product/alllist' : `/eShop/product/typelist?pType=${category}`;
        const response = await axios.get(url);
        if (response.data.code === 200) {
          products.value = response.data.data;
        } else {
          console.error('Failed to fetch products:', response.data.msg);
        }
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };
    const fetchProductsByCategory = category => {
      fetchProducts(category);
    };
    const searchProduct = async () => {
      try {
        const response = await axios.get(`/eShop/product/getproduct`, {
          params: {
            pName: searchQuery.value
          }
        });
        if (response.data.code === 200) {
          products.value = response.data.data;
        } else {
          console.error('Failed to fetch products:', response.data.msg);
        }
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };
    const showProductDetails = product => {
      selectedProduct.value = product;
      showDetails.value = true;
      showPurchaseForm.value = false;
    };
    const formatPath = path => path.replace(/\\/g, '/');
    const decreaseQuantity = () => {
      if (purchaseQuantity.value > 1) {
        purchaseQuantity.value--;
      }
    };
    const increaseQuantity = () => {
      purchaseQuantity.value++;
    };
    const addToCart = async () => {
      const uid = localStorage.getItem('uid');
      if (!uid) {
        console.error('User ID not found in localStorage');
        return;
      }
      const cartData = {
        uid0: uid,
        pid: selectedProduct.value.pid,
        p_id_count: purchaseQuantity.value
      };
      try {
        const response = await axios.post('/eShop/shopcar/addshopcar', cartData);
        if (response.data.code === 200) {
          alert('商品已加入购物车！');
          fetchCartCount(); // 更新购物车数量
        } else {
          alert('加入购物车失败，请重试。');
        }
      } catch (error) {
        console.error('Error adding to cart:', error);
        alert('加入购物车失败，请重试。');
      }
    };
    const confirmPurchase = async () => {
      const uid = localStorage.getItem('uid');
      if (!uid) {
        console.error('User ID not found in localStorage');
        return;
      }
      const orderData = {
        uid0: uid,
        ostate: '0',
        otime: Date.now(),
        pcount: purchaseQuantity.value.toString()
      };
      try {
        const orderResponse = await axios.post('/eShop/orders/addorders', orderData);
        const oid = orderResponse.data.data;
        if (oid) {
          const orderDetailData = {
            oid: oid,
            pid: selectedProduct.value.pid,
            odPCount: purchaseQuantity.value
          };
          const orderDetailResponse = await axios.post('/eShop/orders/addordersdetail', orderDetailData);
          if (orderDetailResponse.data.data) {
            alert('订单提交成功！');
          } else {
            alert('订单提交失败，请重试。');
          }
        } else {
          alert('订单提交失败，请重试。');
        }
      } catch (error) {
        console.error('Error processing purchase:', error);
        alert('订单提交失败，请重试。');
      }
    };
    const fetchCartCount = async () => {
      const uid = localStorage.getItem('uid');
      if (!uid) {
        console.error('User ID not found in localStorage');
        return;
      }
      try {
        const response = await axios.get('/eShop/shopcar/getusershopcarcount', {
          params: {
            uid: uid
          }
        });
        if (response.data.code === 200) {
          cartCount.value = response.data.data;
        } else {
          console.error('Failed to fetch cart count:', response.data.msg);
        }
      } catch (error) {
        console.error('Error fetching cart count:', error);
      }
    };
    const fetchCartItems = async () => {
      const uid = localStorage.getItem('uid');
      if (!uid) {
        console.error('User ID not found in localStorage');
        return;
      }
      try {
        const response = await axios.get('/eShop/shopcar/getusershopcar', {
          params: {
            uid: uid
          }
        });
        if (response.data.code === 200) {
          const items = response.data.data;

          // 获取每个商品的已添加数量
          for (const item of items) {
            try {
              const countResponse = await axios.get('/eShop/shopcar/getproductcount', {
                params: {
                  uid0: uid,
                  pId: item.pid
                }
              });
              if (countResponse.data.code === 200) {
                item.quantity = countResponse.data.data; // 设置默认值为已添加数量
              } else {
                console.error('Failed to fetch product count:', countResponse.data.msg);
                item.quantity = 1; // 如果获取失败，默认值为1
              }
            } catch (error) {
              console.error('Error fetching product count:', error);
              item.quantity = 1; // 如果请求失败，默认值为1
            }
            item.selected = false; // 初始化选中状态为 false
          }
          cartItems.value = items;
        } else {
          console.error('Failed to fetch cart items:', response.data.msg);
        }
      } catch (error) {
        console.error('Error fetching cart items:', error);
      }
    };
    const decreaseCartItemQuantity = item => {
      if (item.quantity > 1) {
        item.quantity--;
      }
    };
    const increaseCartItemQuantity = item => {
      item.quantity++;
    };
    const showCartDialog = () => {
      fetchCartItems();
      showCart.value = true;
    };
    const scrollToTop = () => {
      window.scrollTo({
        top: 0,
        behavior: 'smooth'
      });
    };
    const checkout = async () => {
      const uid = localStorage.getItem('uid');
      if (!uid) {
        console.error('User ID not found in localStorage');
        return;
      }

      // 过滤出选中的商品
      const selectedItems = cartItems.value.filter(item => item.selected);
      if (selectedItems.length === 0) {
        alert('请选择要结算的商品');
        return;
      }

      // 构建请求数据
      const orderData = selectedItems.map(item => ({
        uid0: uid,
        pid: item.pid,
        p_id_count: item.quantity
      }));
      try {
        const response = await axios.post('/eShop/shopcar/ordershopcar', orderData);
        if (response.data.code === 200) {
          alert('订单提交成功！');
          fetchCartItems(); // 重新获取购物车商品以刷新界面
          fetchCartCount(); // 更新购物车数量
        } else {
          alert('订单提交失败，请重试。');
        }
      } catch (error) {
        console.error('Error processing checkout:', error);
        alert('订单提交失败，请重试。');
      }
    };
    const fetchAddresses = async () => {
      const uid = localStorage.getItem('uid');
      if (!uid) {
        console.error('User ID not found in localStorage');
        return;
      }
      try {
        const response = await axios.get('/eShop/address/getuseraddress', {
          params: {
            uid: uid
          }
        });
        if (response.data.code === 200) {
          addresses.value = response.data.data;
        } else {
          console.error('Failed to fetch addresses:', response.data.msg);
        }
      } catch (error) {
        console.error('Error fetching addresses:', error);
      }
    };
    const addAddress = async () => {
      const uid = localStorage.getItem('uid');
      if (!uid) {
        console.error('User ID not found in localStorage');
        return;
      }
      try {
        const response = await axios.post('/eShop/address/add', {
          ...newAddress.value,
          uid: uid
        });
        if (response.data.code === 200) {
          alert('地址添加成功！');
          fetchAddresses(); // 重新获取地址列表
          newAddress.value = {
            uname: '',
            uphone: '',
            addressName: '',
            umail: ''
          }; // 清空表单
        } else {
          alert('地址添加失败，请重试。');
        }
      } catch (error) {
        console.error('Error adding address:', error);
        alert('地址添加失败，请重试。');
      }
    };
    const editAddress = address => {
      // 处理编辑地址的逻辑
      console.log('编辑地址:', address);
    };
    const deleteAddress = async aid => {
      try {
        const response = await axios.delete(`/eShop/address/delete/${aid}`);
        if (response.data.code === 200) {
          alert('地址删除成功！');
          fetchAddresses(); // 重新获取地址列表
        } else {
          alert('地址删除失败，请重试。');
        }
      } catch (error) {
        console.error('Error deleting address:', error);
        alert('地址删除失败，请重试。');
      }
    };
    const toggleMyOptions = () => {
      showMyOptions.value = !showMyOptions.value;
    };
    onMounted(() => {
      const jwt = localStorage.getItem('jwt');
      if (jwt) {
        axios.defaults.headers.common['Authorization'] = `${jwt}`;
      }
      fetchProducts();
      fetchCartCount();
      fetchAddresses();
    });
    return {
      products,
      showDetails,
      selectedProduct,
      categories,
      searchQuery,
      deliveryAddress,
      purchaseQuantity,
      showPurchaseForm,
      cartCount,
      showCart,
      cartItems,
      showMyOptions,
      showAddressDialog,
      showOrderDialog,
      addresses,
      newAddress,
      fetchProductsByCategory,
      searchProduct,
      showProductDetails,
      formatPath,
      decreaseQuantity,
      increaseQuantity,
      addToCart,
      confirmPurchase,
      scrollToTop,
      showCartDialog,
      decreaseCartItemQuantity,
      increaseCartItemQuantity,
      checkout,
      fetchAddresses,
      addAddress,
      editAddress,
      deleteAddress,
      toggleMyOptions
    };
  }
};
